package io.wazo.callkeep;

import android.app.Notification;
import android.app.NotificationChannel;
import android.app.NotificationManager;
import android.app.PendingIntent;
import android.app.Service;
import android.content.Context;
import android.content.Intent;
import android.content.res.Resources;
import android.os.Build;
import android.os.Bundle;
import android.os.IBinder;
import android.util.Log;
import android.view.WindowManager;

import androidx.annotation.Nullable;
import androidx.annotation.RequiresApi;
import androidx.core.app.NotificationCompat;

import com.facebook.react.bridge.ReadableMap;

import java.util.HashMap;

import static io.wazo.callkeep.Constants.EXTRA_CALLER_NAME;
import static io.wazo.callkeep.Constants.EXTRA_CALL_NUMBER;

public class IncomingcallService extends Service {
    @Override
    public int onStartCommand(Intent intent, int flags, int startId) {
        Log.d("incomingcallService", "OnstartCommand");
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            Log.d("incomingcallService", "StartNotification");
            Intent intentIncoming = new Intent( getApplicationContext(), IncomingCallActivity.class );
            Bundle extras = new Bundle();
            String callerName = intent.getStringExtra(EXTRA_CALLER_NAME);
            String number = intent.getStringExtra(EXTRA_CALL_NUMBER);
            extras.putString(EXTRA_CALLER_NAME, callerName);
            extras.putString(EXTRA_CALL_NUMBER, number);
            intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK +
                    WindowManager.LayoutParams.FLAG_SHOW_WHEN_LOCKED +
                    WindowManager.LayoutParams.FLAG_DISMISS_KEYGUARD +
                    WindowManager.LayoutParams.FLAG_TURN_SCREEN_ON +
                    WindowManager.LayoutParams.FLAG_KEEP_SCREEN_ON);
            intent.putExtras(extras);
            Notification notification = buildNotification(intentIncoming);
            startForeground(1, notification);
            sendBroadcast(new Intent(Intent.ACTION_CLOSE_SYSTEM_DIALOGS));
            return START_NOT_STICKY;
        }

        return super.onStartCommand(intent, flags, startId);
    }

    private Notification buildNotification(Intent intent) {
        if (Build.VERSION.SDK_INT < Build.VERSION_CODES.O) return null;
        String NOTIFICATION_CHANNEL_ID = "com.lachong.toda";
        String channelName = "Foreground service for toda";
        NotificationChannel chan = new NotificationChannel(NOTIFICATION_CHANNEL_ID, channelName, NotificationManager.IMPORTANCE_HIGH);
        chan.setLockscreenVisibility(Notification.VISIBILITY_PRIVATE);
        NotificationManager manager = (NotificationManager) getSystemService(Context.NOTIFICATION_SERVICE);
        assert manager != null;
        manager.createNotificationChannel(chan);
        PendingIntent fullScreenPendingIntent = PendingIntent.getActivity(this, 0, intent, PendingIntent.FLAG_UPDATE_CURRENT);
        NotificationCompat.Builder notificationBuilder = new NotificationCompat.Builder(this, NOTIFICATION_CHANNEL_ID);
        notificationBuilder
                .setAutoCancel(true)
                .setContentTitle("Cuộc gọi đến")
                .setPriority(NotificationManager.IMPORTANCE_HIGH)
                .setCategory(Notification.CATEGORY_CALL)
                .setFullScreenIntent(fullScreenPendingIntent, true);

        return notificationBuilder.build();
    }

    @Nullable
    @Override
    public IBinder onBind(Intent intent) {
        return null;
    }
}
